//        do {
//            vs = try JSONDecoder().decode([Verb].self, from: data);
//        } catch {
//            print(error);
//        }
        
        var index: Int
        
        for _ in 0..<numVerbs {
            index = Int.random(in: 0...(vs.count - 1))
            verbsSelected.append(vs[index])
            vs.remove(at: index)
        }
        
        return verbsSelected
